
public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=40;
		
		int c=Summation(a,b);
		System.out.println("The sum of a and b is: "+c);

	}
	
	public static int Summation(int a, int b) {
		int s;
		s=a+b;
		return s;
	}

}
